    padding:0;
    line-height:60px;
}

.content-b{
    width:100%;
    height:90%;
    padding-top:10px;
}
.el-header{
    background-color: #ccc;
    color: #323232;
    text-align: left;
    line-height: 60px;
    margin:10px 0 20px;
    font-size:26px;
    font-weight:bold;
    float:left;
    padding:0;
}
aside.el-aside{
    width:255px !important;
}
