    background-color: #cbe198;
    color: #333;
    text-align: center;
    line-height: 200px;
    width:255px;
    /* height:430px; */
    margin:0 10px;
}
.el-main {
    background-color: #cbe198;
    color: #333;
    text-align: center;
    line-height: 160px;
}
.el-header>div>img{
    margin:20px;
    float:left;
    margin-left:30px; 
}
main.el-main{
    padding: 0;
}
.chat-a,.chat-b,.chat-a>p{
    width:100%;height:40px;
    background-color: #add597;
    font-size: 20px;
    line-height: 40px;
}
