.content-title {
	width: 100%;
	height: 60px;
	background: #c0c0c0;
	margin-top: 10px;
}

.content-title>div {
	width: 250px;
	height: 100%;
	float: left;
}

.content-title>div>img {
	margin: 20px;
	float: left;
	margin-left: 30px;
}

.content-title>div>p {
	font-size: 26px;
	font-weight: bold;
	float: left;
	padding: 0;
	line-height: 60px;
}

.content-b {
	width: 100%;
	height: 90%;
	padding-top: 10px;
}

.el-header {
	background-color: #ccc;
	color: #323232;
	text-align: left;
	line-height: 60px;
	margin: 10px 0 20px;
	font-size: 26px;
	font-weight: bold;
	float: left;
	padding: 0;
}

aside.el-aside {
	width: 255px !important;
}

.el-aside {
	background-color: #cbe198;
	color: #333;
	text-align: center;
	line-height: 200px;
	width: 255px;
	/* height:430px; */
	margin: 0 10px;
}

.el-main {
	background-color: #cbe198;
	color: #333;
	text-align: center;
	line-height: 160px;
}

.el-header>div>img {
	margin: 20px;
	float: left;
	margin-left: 30px;
}

main.el-main {
	padding: 0;
}

.chat-a,
.chat-b,
.chat-a>p {
	width: 100%;
	height: 40px;
	background-color: #add597;
	font-size: 20px;
	line-height: 40px;
}

.chat-b {
	position: relative;
}

.chat-a>p {
	/* position: fixed;
    top:0;left:0; */
	color: #fff;
	/* z-index: 2; */
}

.chat-b>p {
	color: #000;
	font-size: 20px;
}

.chat-list,
.chat-msg {
	width: 100%;
	height: 385px;
	min-height: 385px;
	/* border:1px solid rosybrown; */
}

