	margin-top: 10px;
}

.chat-msg>.chat-msg-b {
	float: right;
}

.chat-msg>.chat-msg-b>img {
	width: 50px;
	height: 50px;
	float: right;
	margin: 15px;
}

.chat-msg-a>.msg-list {
	float: left;
	width: 490px;
	/* height:80px; */
	/* border:1px solid #dae8ba; */
