	background-size: cover;
	-moz-background-size: cover;
	margin-top: -20px;
	/* display: inline; */
}

.back-img-right {
	float: right;
	/* width:150px; */
	height: 100%;
	background: url('../../../public/images/chatright.png') center center no-repeat;
	/* background-size:100% 100%;  */
	/* background-position: center; */
	background-size: cover;
	-moz-background-size: cover;
	/* margin-top:-20px; */
}

.back-img>p,
.back-img-right>p {
	/* border:1px solid yellowgreen; */
	height: 80px;
	padding: 0;
	line-height: 80px;
